create extension if not exists "uuid-ossp"; create table if not exists public.profiles ( id uuid primary key default uuid_generate_v4(), name text, slug text unique not null, data jsonb, verification_level int default 0, verification_token text, created_at timestamptz default now(), updated_at timestamptz default now()); alter table public.profiles enable row level security; create policy "public read" on public.profiles for select using (true); create policy "auth write (MVP)" on public.profiles for all to authenticated using (true) with check (true);